package htun

import (
	"fmt"
	"log"
	"strings"
	"testing"
)

func TestRead(t *testing.T) {
	var (
		err error
		cr  = NewChunkReader(strings.NewReader(""))
	)
	defer func() {
		if err != nil {
			log.Println(err)
		}
	}()

	buffer := make([]byte, 1024)
	n := 0
	for {
		n, err = cr.Read(buffer)
		if n > 0 {
			fmt.Print(string(buffer[:n]))
		}

		if err != nil {
			return
		}
	}
}
